import java.util.Properties;        import java.sql.Connection;        import java.sql.Statement;        import java.sql.ResultSet;        import java.sql.DriverManager;        import java.io.File;        import java.io.FileInputStream;        import java.io.DataInputStream;        import java.util.Base64;        import java.security.spec.PKCS8EncodedKeySpec;        import java.security.KeyFactory;        import java.security.PrivateKey;        import javax.crypto.EncryptedPrivateKeyInfo;        import javax.crypto.SecretKeyFactory;        import javax.crypto.spec.PBEKeySpec;
public class TestJdbc        {            public static void main(String[] args)                throws Exception            {              File f = new File("<path>/rsa_key.p8");              FileInputStream fis = new FileInputStream(f);              DataInputStream dis = new DataInputStream(fis);              byte[] keyBytes = new byte[(int) f.length()];              dis.readFully(keyBytes);              dis.close();

String encrypted = new String(keyBytes);              String passphrase = System.getenv("PRIVATE_KEY_PASSPHRASE");              encrypted = encrypted.replace("-----BEGIN ENCRYPTED PRIVATE KEY-----", "");              encrypted = encrypted.replace("-----END ENCRYPTED PRIVATE KEY-----", "");              EncryptedPrivateKeyInfo pkInfo = new EncryptedPrivateKeyInfo(Base64.getMimeDecoder().decode(encrypted));              PBEKeySpec keySpec = new PBEKeySpec(passphrase.toCharArray());              SecretKeyFactory pbeKeyFactory = SecretKeyFactory.getInstance(pkInfo.getAlgName());              PKCS8EncodedKeySpec encodedKeySpec = pkInfo.getKeySpec(pbeKeyFactory.generateSecret(keySpec));              KeyFactory keyFactory = KeyFactory.getInstance("RSA");              PrivateKey encryptedPrivateKey = keyFactory.generatePrivate(encodedKeySpec);
      String url = "jdbc:snowflake://<account>.snowflakecomputing.com";              Properties prop = new Properties();              prop.put("user", "<user>");              prop.put("account", "<account>");              prop.put("privateKey", encryptedPrivateKey);
      Connection conn = DriverManager.getConnection(url, prop);              Statement stat = conn.createStatement();              ResultSet res = stat.executeQuery("select 1");              res.next();              System.out.println(res.getString(1));              conn.close();            }        }
Key Rotation
Snowflake supports multiple active keys to allow for uninterrupted rotation. Rotate and replace your public and private keys based on the expiration schedule you follow internally.
Currently, you can use the RSA_PUBLIC_KEY and RSA_PUBLIC_KEY_2 parameters for ALTER USER to associate up to 2 public keys with a single user.
